import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { 
  Container, 
  Typography, 
  TextField, 
  Button, 
  Box, 
  Paper, 
  Alert 
} from '@mui/material'; // Correct package name

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [attemptsLeft, setAttemptsLeft] = useState(3);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json(); // Parse the response body as JSON
      console.log('r:', response.ok);
      if (!response.ok) {
        // Handle rate limit error or other errors
        console.log('Error from backend:', data.error);
        if (data.error) {
          //setError(data.error); // Display the error message from the backend
          if (data.error === "Too many attempts. Try again later.") {
            setAttemptsLeft(0); // Disable the login button
          }
    
        }else {
          setError('Login failed. Please try again.');
        }
        return;
      }

      localStorage.setItem('token', data.token); // Save the token to localStorage
      navigate('/products'); // Redirect to the products page
    } catch (err) {
      setError(
        err.res?.data?.message ||
        err.message ||
        'Login failed'
      );
    }
  };

  return (
    <Container maxWidth="sm">
      <Box
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          minHeight: '100vh',
        }}
      >
        <Paper elevation={3} sx={{ padding: 4, width: '100%', maxWidth: 400 }}>
          <Typography variant="h4" align="center" gutterBottom>
            Login
          </Typography>
          {error && (
          <Alert severity="error" sx={{ mt: 2 }}>
            {error}
          </Alert>
        )}

          <form onSubmit={handleSubmit}>
            <TextField
              label="Username"
              fullWidth
              margin="normal"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
            <TextField
              label="Password"
              type="password"
              fullWidth
              margin="normal"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <Button
              type="submit"
              variant="contained"
              color="primary"
              fullWidth
              sx={{ mt: 2 }}
              disabled={attemptsLeft === 0}
            >
              {attemptsLeft === 0 ? 'Try again later' : 'Login'}
            </Button>

            
          </form>
        </Paper>
      </Box>
    </Container>
  );
};

export default Login;
